function.block:
{

        // store the old value
        boolean oldTrackPositionLeading = m_trackPositionLeading;

        // set the new value
        m_trackPositionLeading = trackPositionLeading;

        // update things if necessary
        if (oldTrackPositionLeading != m_trackPositionLeading) {
            configureSubcomponents();
            firePropertyChange(
                PROPERTY_TRACKPOSITIONLEADING,
                oldTrackPositionLeading, m_trackPositionLeading
            );
        }

    }
